story.text:
export makeScene(function* (view) {
  const circle = createRef<Circle>();
  view.add(
    <Circle
       ref={circle}
       width={320}
       height={320}
       fill={'blue'}
     />
  );
  yield* circle().scale(2, 0.3);
  yield* waitUntil('event');
  yield* all(
    circle().scale(1, 0.3),
    circle().position.y(200, 0.3),
  );
  yield* circle().fill('green', 0.3);
});

